import React, {useState} from "react";
import Room from "./Room";
import info from "../info";


function createRoom(info) {
    return (
      <Room
        key={info.id}
        roomId = {info.id}
        temp={info.temp}
        hub={info.hub}
        last_active={info.last_active}
      />
    );
   }

